CREATE DEFINER=`root`@`localhost` PROCEDURE `load_all`()
BEGIN
	select POST.id, POST.title,POST.des,USER.username,CATEGORY.name,POST.time, POST.img
	from POST inner join USER
			on POST.user =USER.id inner join CATEGORY
				on POST.category=CATEGORY.id
	order by time
    limit 10;
END ;;
DELIMITER ;
