CREATE DEFINER=`root`@`localhost` PROCEDURE `user_bookmark`(puser int)
BEGIN
	select POST.id,POST.title,POST.des,POST.time, POST.img,CATEGORY.name, USER.name as username
	from POST inner join BOOKMARK
    on POST.id=BOOKMARK.postid inner join CATEGORY on CATEGORY.id=POST.category
    inner join USER on USER.id = POST.user
    where BOOKMARK.userid=puser;
END ;;
DELIMITER ;
